        [Fact]
        public void CompressionTestShort()
        {
            var uncompressed = Encoding.UTF8.GetBytes("This is a test string which repeats. This is a test string which repeats. This is a test string which repeats. ");

            var compressed = MiniLZO.Compress(uncompressed);

            var compressedExpected = new byte[] { 0x0, 0x18, 0x54, 0x68, 0x69, 0x73, 0x20, 0x69, 0x73, 0x20, 0x61, 0x20, 0x74, 0x65, 0x73, 0x74, 0x20, 0x73, 0x74, 0x72, 0x69, 0x6e, 0x67, 0x20, 0x77, 0x68, 0x69, 0x63, 0x68, 0x20, 0x72, 0x65, 0x70, 0x65, 0x61, 0x74, 0x73, 0x2e, 0x20, 0x54, 0x68, 0x69, 0x73, 0x20, 0x20, 0x13, 0x90, 0x0, 0xe, 0x67, 0x20, 0x77, 0x68, 0x69, 0x63, 0x68, 0x20, 0x72, 0x65, 0x70, 0x65, 0x61, 0x74, 0x73, 0x2e, 0x20, 0x11, 0x0, 0x0 };
            Assert.Equal<byte>(compressed, compressedExpected);
        }

        [Theory]
        [InlineData(new byte[] { 55, }, new byte[] { 18, 55, 17, 0, 0 })]
        [InlineData(new byte[] { 23, 155 }, new byte[] { 19, 23, 155, 17, 0, 0 })]
        [InlineData(new byte[] { 1, 2, 3 }, new byte[] { 20, 1, 2, 3, 17, 0, 0 })]
        public void CompressionTestVeryShort(byte[] uncompressed, byte[] compressedExpected)
        {
            var compressed = MiniLZO.Compress(uncompressed);
            Assert.Equal<byte>(compressed, compressedExpected);
        }

        [Fact]
        public void CompressionTestZeroLength()
        {
            var uncompressed = Array.Empty<byte>();

            var compressed = MiniLZO.Compress(uncompressed);

            var compressedExpected = new byte[] { 17, 0, 0 };
            Assert.Equal<byte>(compressed, compressedExpected);
        }

        [Fact]
        public void CompressionTestRepeatingCharacter()
        {
            var uncompressed = new byte[200];

            for (var i = 0; i < uncompressed.Length; i++)
                uncompressed[i] = 0x55;

            var compressed = MiniLZO.Compress(uncompressed);

            foreach (var d in compressed)
                Debug.Write($"0x{d:x}, ");

            var compressedExpected = new byte[] { 0x2, 0x55, 0x55, 0x55, 0x55, 0x55, 0x20, 0x8f, 0x10, 0x0, 0x0, 0x1, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x55, 0x11, 0x0, 0x0 };
            Assert.Equal<byte>(compressed, compressedExpected);
        }
